isManager = false
for (PrismContainerValue assignment in assignmnetsToModify) {
    AssignmentType ass = assignment.asContainerable();
	if (ass.getTargetRef() != null && ass.getTargetRef().getOid().equals("6aaaa771-3267-454f-b399-4a84ddfc78f8")) {
